import React, { Component } from 'react'
import './index.css'

export default class Footer extends Component {



  render() {
    const {todos} = this.props;
    const countOfDone = todos.reduce((pre,cur) => {
        return pre + ((cur.done)? 1 : 0 )
    },0)
    return (
        <div className="todo-footer">
            <label>       
                <input type='checkbox' checked={countOfDone === todos.length ? true : false}/>
            </label>
            <span>
            <span>Complete : {countOfDone}</span> /  All : {todos.length}
            </span>
            <button className="btn btn-danger">Clean completed tasks</button>
        </div>
    );
  }
}
